mapper.select:
SELECT id, user_id, meal_type, food_code, food_name, default_grams, created_at
FROM meal_todos
WHERE user_id = #{userId}
  AND meal_type = #{mealType}
ORDER BY created_at DESC

mapper.insert:
INSERT INTO meal_todos (
    user_id, meal_type, food_code, food_name, default_grams
) VALUES (
             #{userId}, #{mealType}, #{foodCode}, #{foodName}, #{defaultGrams}
         )

mapper.select:
SELECT id, user_id, meal_type, food_code, food_name, default_grams, created_at
FROM meal_todos
WHERE id = #{id}
  AND user_id = #{userId}

mapper.delete:
DELETE FROM meal_todos
WHERE id = #{id}
  AND user_id = #{userId}
