mapper.select:
SELECT id, email, password, name
FROM user
WHERE email = #{email} AND password = #{password}

SELECT id, email, password, name, phone, gender, age
FROM user
WHERE email = #{email}

SELECT id, email, password, name, phone, gender, age
FROM user
WHERE id = #{id}

mapper.update:
UPDATE user
SET name = #{name},
    phone = #{phone},
    gender = #{gender},
    age = #{age}
WHERE id = #{id}

mapper.select:
SELECT id, user_id, height, weight, diabetes, high_blood_pressure, hyperlipidemia, kidney_disease, activity_level
FROM user_health
WHERE user_id = #{userId}
